#include<stdlib.h>#include<iostream>#define ElemType int    //初始化数据类型 #define listsize_MAX 100//初始空间分配量#define ok 1#define no 0using namespace std;typedef struct {	ElemType* elem;//地址 	int length;//长度 	int listsize; //最大长度 }SqList;int InitList(SqList& L) {//线性表初始化 	L.elem = (ElemType*)malloc(listsize_MAX * sizeof(ElemType));//malloc需要 #include<stdlid.h>	if (!L.elem)exit(0);//指针为NULL相当于指针等于零，所以L.elem==NULL则!L.elem为真	L.length = 0;	L.listsize = listsize_MAX;	return ok;}void add(SqList& L, int num) {//添加数据 ,添加次数为num 	L.length = num;	for (int i = 0; i < L.length; i++) {//一个一个添加		cout << "连续输入" << num << "个数字加入数据库,每次只输入一个" << endl;		cin >> L.elem[i];	}}void ListOutput(SqList& L) {//输出数据	cout << "输出数据" << endl;	for (int i = 0; i < L.length; i++) {//遍历线性表		cout << L.elem[i] << '\t';	}}int main() {	int num;//定义添加多少个数据	SqList L1;//定义线性表	InitList(L1);//初始化	cout << "请输入你要添加多少个数据进去，不得超过100" << endl;	cin >> num;//你要添加多少个数据进去	add(L1, num);//添加数据	ListOutput(L1);//输出数据	return 0;}